INSERT INTO `fs_config_en` VALUES (18, 'content_recruitment', 'Nội dung trang tuyển dụng', '<p><span style=\"font-size:18px;margin-bottom:20px; line-height:40px;\"><strong>WORKING AT SSP</strong></span></p>\r\n\r\n<p style=\"margin-bottom:20px;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendung2.png\" style=\"width: 20px; height: 19px;\" /><span style=\"font-size:16px;\"><strong>&nbsp;<span style=\"color:#0e497a;\"></span><span style=\"color:#0e497a;\">SSP people</span><span style=\"color:#0e497a;\"></span></strong></span></p>\r\n\r\n<p style=\"font-size: 16px; color: rgb(51, 51, 51); line-height: 35px; text-align: justify;\">Realized that human resources are the most valuable resource, the center of development, SSP Company always creates conditions for all employees to fulfill their potential, enthusiasm, and creativity with a good spirit, lean thinking, continuous improvement. At the same time, we build habits, working methods, manners, and civilized, polite behavior.</p>\r\n\r\n<p style=\"margin-bottom:20px;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendung.png\" style=\"width: 19px; height: 15px;\" /><span style=\"color:#0e497a;\"><span style=\"font-size:16px;\"><strong>&nbsp;SSP culture</strong></span></span></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_03.jpg\" style=\"width: 181px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><b>For co-workers</b></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_05.jpg\" style=\"width: 180px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>For superiors, subordinates</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_07.jpg\" style=\"width: 180px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Work in groups</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<ul class=\"tuyendung\">\r\n	<li style=\"text-align: justify;\"><strong>For co-workers: </strong>Respect and cooperate to help each other for completing the job and maintaining a friendly working environment.</li>\r\n	<li style=\"text-align: justify;\"><strong>For superiors, subordinates: </strong>Subordinates always obey the direction and mobilization of the superiors as well as the company in their work. Also, we appreciate the continuous innovation of each employee</li>\r\n	<li style=\"text-align: justify;\"><strong>Work in groups: </strong>A united and effective teamwork plays an important role in success. Therefore, the company always creates conditions for individuals to work in groups to support and help each other to achieve good results. The results achieved by the group are the result of each individual. The contribution and cooperation of each member in each group will create the strength of the whole company.</li>\r\n</ul>\r\n<style type=\"text/css\">.tuyendung li{list-style-image:url(../upload_images/images/2020/02/26/li-tuyendung.jpg); font-size:16px; color:#333333; line-height:35px; margin-left:20px;margin-bottom:20px;}\r\n</style>', 'editor', 1, 1, NULL, 18);
INSERT INTO `fs_config_en` VALUES (20, 'content_employees', 'Nội dung trang Việc làm', '<p><span style=\"font-size:18px;margin-bottom:20px; line-height:40px;\"><strong>LÀM VIỆC TẠI SSP</strong></span></p>\r\n\r\n<p style=\"margin-bottom:20px;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendung2.png\" style=\"width: 20px; height: 19px;\" /><span style=\"font-size:16px;\"><strong>&nbsp;<span style=\"color:#0e497a;\">Con người SSP&nbsp;</span></strong></span></p>\r\n\r\n<p style=\"font-size: 16px; color: rgb(51, 51, 51); line-height: 35px; text-align: justify;\">Nhận thức được rằng,con người là nguồn lực quý giá nhất, trung tâm của sự phát triển, Công ty SSP luôn tạo mọi điều khiện để mọi nhân viên phát huy hết tiềm năng, sự nhiệt huyết và sáng tạo của mình bằng một Tâm thế tốt, tư duy tinh gọn, cải tiến liên tục. Đồng thời hình thành những thói quen, lề lối làm việc, phương cách ứng xử, cùng hành vi văn minh, lịch sự.&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:20px;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendung.png\" style=\"width: 19px; height: 15px;\" /><span style=\"color:#0e497a;\"><span style=\"font-size:16px;\"><strong>&nbsp;Văn hóa SSP</strong></span></span></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_03.jpg\" style=\"width: 181px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><b>Đồng nghiệp</b></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_05.jpg\" style=\"width: 180px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Cấp trên, Cấp dưới</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/tuyendungv2_07.jpg\" style=\"width: 180px; height: 112px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Làm việc theo nhóm</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<ul class=\"tuyendung\">\r\n	<li style=\"text-align: justify;\"><strong>Với đồng nghiệp:</strong> Tôn trọng và hợp tác giúp đỡ lẫn nhau vì mục đích hoàn thành công việc và duy trì môi trường làm việc thân thiện.</li>\r\n	<li style=\"text-align: justify;\"><strong>Với cấp trên, cấp dưới:</strong> cấp dưới luôn tuân thủ sự chỉ đạo, điều động của cấp trên cũng như của công ty trong công việc. Đồng thời, luôn đề cao sự cải tiến sáng tạo không ngừng</li>\r\n	<li style=\"text-align: justify;\"><strong>Làm việc theo nhóm:</strong> Một tập thể làm việc đoàn kết và hiệu quả là yếu tố thiết yếu cho sự thành công. Vì vậy Công ty luôn tạo điều kiện để các cá nhân làm việc theo nhóm nhằm hỗ trợ, bổ sung, giúp đỡ lẫn nhau đạt được kết quả tốt. Kết quả đạt được của nhóm chính là thành quả của mỗi cá nhân. Sự đóng góp và hợp tác của từng thành viên trong từng nhóm sẽ tạo ra sức mạnh của cả Công ty.</li>\r\n</ul>\r\n<style type=\"text/css\">.tuyendung li{list-style-image:url(../upload_images/images/2020/02/26/li-tuyendung.jpg); font-size:16px; color:#333333; line-height:35px; margin-left:20px;margin-bottom:20px;}\r\n</style>', 'editor', 1, 1, NULL, 15);
