INSERT INTO `fs_contents` VALUES (1, 'Thay mặt cho toàn thể CBCNV Công ty SSP, tôi xin gửi lời chào trân trọng và thân thiết nhất đến Quý khách hàng. Chúc quý khách hàng một năm hoạt động kinh doanh phát đạt và thịnh vượng.', '<p style=\"text-align: justify;\">Thay mặt cho toàn thể CBCNV Công ty SSP, tôi xin gửi lời chào trân trọng và thân thiết nhất đến Quý khách hàng. Chúc quý khách hàng một năm hoạt động kinh doanh phát đạt và thịnh vượng.</p>\r\n\r\n<p style=\"text-align: justify;\">Công ty TNHH SSP Moulding chuyên về ép nhựa kỹ thuật, có khả năng đối&nbsp;ứng từ chế tạo thử đến sản xuất hàng loạt các loại linh kiện nhựa chất lượng cao. Sản phẩm chính của SSP là các loại linh kiện ép nhựa phục vụ&nbsp;cho&nbsp;các&nbsp;lĩnh vực đa dạng như ô tô, xe máy, điện dân dụng.v.v. Với mục tiêu sẽ trở thành công ty hàng đầu trong lĩnh vực ép nhựa độ chính xác cao tại Việt Nam,chúng tôi đang sở hữu và không ngừng cải tiến các dây chuyền sản xuất, hệ thống quản lý chất lượng, nhằm đem lại hiệu suất cao trong sản xuất.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Với tâm thế giảm thiểu lãng phí, nâng cao hiệu suất, SSP tin rằng sẽ trở thành doanh nghiệp tiên phong trong cách suy nghĩ và tổ chức&nbsp;sản xuất mới tại Việt Nam, tạo ra các sản phẩm chất lượng cao vươn ra thị trường thế giới.</p>\r\n\r\n<p style=\"text-align: right;\"><strong>Giám đốc&nbsp; &nbsp; &nbsp; &nbsp;</strong></p>\r\n\r\n<p style=\"text-align: right;\"><strong>Nguyễn Thế Hùng</strong></p>', NULL, 1, 'gioi-thieu-cong-ty', 'Giới thiệu công ty', ',1,', ',gioi-thieu-cong-ty,', 1, 'Lời chào giám đốc', 'loi-chao-giam-doc', 'images/contents/2020/02/25/original/icon_57_1582601936.jpg', NULL, NULL, '2017-08-14 18:54:16', '2020-03-26 11:32:34', NULL, 1, 0, 1, 5, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Lời chào giám đốc', 'Lời chào giám đốc', 'Thay mặt cho toàn thể CBCNV Công ty SSP, tôi xin gửi lời chào trân trọng và thân thiết nhất đến Quý khách hàng. Chúc quý khách hàng một năm hoạt động kinh doanh phát đạt và thịnh vượng.', NULL, 0, 'admin', 'admin');
INSERT INTO `fs_contents` VALUES (2, '', '<p style=\"text-align: justify;\">Công ty TNHH SSP Moulding, có nhà máy đặt tại Khu Công nghiệp Bình Xuyên, tỉnh Vĩnh Phúc là đơn vị chuyên về ép nhựa kỹ thuật, có khả năng đáp ứng các yêu cầu của khách hàng từ chế tạo thử đến sản xuất hàng loạt các sản phẩm phụ tùng nhựa với chất lượng và giá trị gia tăng cao.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"color:#0e497a;\"><strong>Tầm nhìn</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu2.jpg\" style=\"width: 785px; height: 306px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" /><span style=\"color:#0e497a;\"><strong>&nbsp;<span style=\"font-size:16px;\">Sứ mệnh</span></strong></span></p>\r\n\r\n<p>Đóng góp cho ngành công nghiệp Việt Nam và xã hội Việt Nam những sản phẩm được làm ra bởi công nghệ cao, góp phần làm thay đổi hình ảnh doanh nghiệp Việt</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"font-size:16px;\"><span style=\"color:#0e497a;\"><strong>Giá trị cốt lõi</strong></span></span></p>\r\n\r\n<p>Tâm thế tốt, tư duy tinh gọn, cải tiến liên tục</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"font-size:16px;\"><span style=\"color:#0e497a;\"><strong>Phương châm hoạt động</strong></span></span></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_03.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Sáng tạo</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_05.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Tốc độ</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_07.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Kiên trì</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>', NULL, 1, 'gioi-thieu-cong-ty', 'Giới thiệu công ty', ',1,', ',gioi-thieu-cong-ty,', 1, 'Về công ty SSP', 've-cong-ty-ssp', 'images/contents/2017/08/14/original/functional_task_1502722092.jpg', NULL, NULL, '2017-08-14 21:48:13', '2020-03-26 11:03:30', NULL, 0, 0, 1, 2, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'Chương trình đào tạo', 'Chương trình đào tạo', 'Chương trình đào tạo', NULL, 0, 'admin', 'admin');
