INSERT INTO `fs_contents` VALUES (9, '', '<p><span style=\"font-size:18px;\"><strong>1. Dây chuyền sản xuất đề cao hiệu suất</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-tuyendung.jpg\" style=\"width: 13px; height: 14px;\" />&nbsp;<strong><span style=\"color:#0e497a;\">HỆ THỐNG SẢN XUẤT KHÉP KÍN TỰ ĐỘNG HÓA CAO</span></strong></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc1.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Hệ thống máy ép phun nhựa 10 tấn ~ 450 tấn</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc2.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Hệ thống robot tự động gắp sản phẩm</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc3.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Màn hình điều khiển đa chức năng</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-tuyendung.jpg\" style=\"width: 13px; height: 14px;\" />&nbsp;<span style=\"color:#0e497a;\"><strong>MÔ HÌNH SẢN XUẤT</strong></span></p>\r\n\r\n<div class=\"mo-hinh\">\r\n<div class=\"row\">\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-1.png\" /></p>\r\n\r\n<p><strong>Kho nguyên liệu</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-2.png\" /></p>\r\n\r\n<p><strong>Công đoạn sản xuất<br />\r\n(Đúc/in,lắp ráp)</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten mobile-hidden\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"pc-hidden\">&nbsp;</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-3.png\" /></p>\r\n\r\n<p><strong>Kiểm soát chất lượng lần cuối</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-4.png\" /></p>\r\n\r\n<p><strong>Đóng gói</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"clearfix row-2 row\">\r\n<div class=\"mo-hinh-item-hidden\">&nbsp;</div>\r\n\r\n<div class=\"mo-hinh-muiten mobile-hidden\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-5.png\" /></p>\r\n\r\n<p><strong>Lưu kho</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-7.png\" /></p>\r\n\r\n<p><strong>Xuất hàng</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>2. Nhà máy phát triển toàn diện</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-tuyendun