INSERT INTO `fs_contents_en` VALUES (1, 'On behalf of all SSP employees, I would like to send the most respectful and friendly greetings to our valued customers. Warmest wishes to you and wish you a prosperous Happy New Year.', '<p style=\"text-align: justify;\">On behalf of all SSP employees, I would like to send the most respectful and friendly greetings to our valued customers. Warmest wishes to you and wish you a prosperous Happy New Year.</p>\r\n\r\n<p style=\"text-align: justify;\">SSP Molding Co., Ltd specializing in the technical plastic injection is able to meet customer requirements from trial production to mass production of plastic parts with high quality. The main products of SSP are plastic parts for motorcycles, cars, household electrical appliances, etc. With the aim of becoming a leading company in the field of plastic injection with high precision in Vietnam, we own and continuously upgrade our production lines, quality management system to reach high productivity.</p>\r\n\r\n<p style=\"text-align: justify;\">SSP is proud of the capacity of cutting waste thoroughly, thereby improving production efficiency. We believe that we are becoming a pioneer company of stable development among manufacturing organizations in Vietnam in order to create high-quality products for the international market.</p>\r\n\r\n<p style=\"text-align: right;\"><strong>Director&nbsp; &nbsp; &nbsp; &nbsp;</strong></p>\r\n\r\n<p style=\"text-align: right;\"><strong>Nguyen The Hung</strong></p>', NULL, 1, 'gioi-thieu-cong-ty', 'Giới thiệu công ty', ',1,', ',gioi-thieu-cong-ty,', 1, 'Director\'s Greeting', 'loi-chao-giam-doc', 'images/contents/2020/02/25/original/icon_57_1582601936.jpg', NULL, NULL, '2017-08-14 18:54:16', '2020-03-30 15:17:08', NULL, 1, 0, 1, 5, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', 'On behalf of all SSP employees, I would like to send the most respectful and friendly greetings to our valued customers. Warmest wishes to you and wish you a prosperous Happy New Year.', NULL, 0, 'admin', 'admin');
INSERT INTO `fs_contents_en` VALUES (2, '', '<p style=\"text-align: justify;\">SSP Molding Co., Ltd., whose factory is located in Binh Xuyen Industrial Zone, Vinh Phuc Province, is a company specializing in technical plastic injection. SSP Moulding is able to meet customer requirements from trial production to mass production of plastic parts with high quality and high added value.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"font-size:16px;\"><span style=\"color:#0e497a;\"><strong>Vision</strong></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/24/tam-nhin-en(1).jpg\" style=\"width: 776px; height: 313px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" /><span style=\"color:#0e497a;\"><strong>&nbsp;<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">Mission</span><span style=\"font-size:16px;\"></span></strong></span></p>\r\n\r\n<p>Contributing to the Vietnamese industry and society products made by high technology in order to change the image of Vietnamese companies.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"font-size:16px;\"><span style=\"color:#0e497a;\"><strong>Core values</strong></span></span></p>\r\n\r\n<p>Good spirit, lean thinking, continuous innovation</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu.png\" style=\"width: 28px; height: 18px;\" />&nbsp;<span style=\"font-size:16px;\"><span style=\"color:#0e497a;\"><strong>Operation motto</strong></span></span></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_03.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Creativity</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_05.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Speed</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/02/26/gioithieu_07.jpg\" style=\"width: 280px; height: 158px;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Patience</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>', NULL, 1, 'gioi-thieu-cong-ty', 'Giới thiệu công ty', ',1,', ',gioi-thieu-cong-ty,', 1, 'About SSP Company', 'about-ssp-company', 'images/contents/2017/08/14/original/functional_task_1502722092.jpg', NULL, NULL, '2017-08-14 21:48:13', '2020-03-26 11:05:00', NULL, 0, 0, 1, 2, NULL, 1, NULL, NULL, NULL, NULL, NULL, 'About SSP Company', 'About SSP Company', 'About SSP Company', NULL, 0, 'admin', 'admin');
