INSERT INTO `fs_contents_en` VALUES (9, '', '<p><span style=\"font-size:18px;\"><strong>1. Production line with high productivity</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-tuyendung.jpg\" style=\"width: 13px; height: 14px;\" /><span style=\"color:#0e497a;\">&nbsp;<strong>HIGH AUTOMATICALLY CLOSED PRODUCTION SYSTEM</strong></span></p>\r\n\r\n<div class=\"btgrid\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc1.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Plastic injection machine: 10 ~ 450 tons</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc2.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Automatic robot for holding products</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-4\">\r\n<div class=\"content\">\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/upload_images/images/2020/03/11/nang-luc3.png\" style=\"width: 370px; height: 208px;\" /></p>\r\n\r\n<p style=\"text-align: center;\">Multi-function control panel</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-tuyendung.jpg\" style=\"width: 13px; height: 14px;\" />&nbsp;<span style=\"color:#0e497a;\"><strong>PRODUCTION PROCESS</strong></span></p>\r\n\r\n<div class=\"mo-hinh\">\r\n<div class=\"row\">\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-1.png\" /></p>\r\n\r\n<p><strong>Material stock</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-2.png\" /></p>\r\n\r\n<p><strong>Manufacturing stage (Molding/printing, assembling)</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten mobile-hidden\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"pc-hidden\">&nbsp;</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-3.png\" /></p>\r\n\r\n<p><strong>Final quality control</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-4.png\" /></p>\r\n\r\n<p><strong>Package</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"clearfix row-2 row\">\r\n<div class=\"mo-hinh-item-hidden\">&nbsp;</div>\r\n\r\n<div class=\"mo-hinh-muiten mobile-hidden\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-5.png\" /></p>\r\n\r\n<p><strong>Warehousing</strong></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"mo-hinh-muiten\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-8.png\" /></p>\r\n</div>\r\n\r\n<div class=\"mo-hinh-item\">\r\n<div class=\"mo-hinh-content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/03/11/icon-7.png\" /></p>\r\n\r\n<p><strong>Delivery</strong></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>2. Comprehensively developed factory</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2020/02/26/li-t